void Screen_ResearchViewBase::setupScreen()
{

}

void Screen_ResearchViewBase::buttonCallbackHandler(const touchgfx::AbstractButton& src)
{
    if (&src == &button_ReturnToTheLast)
    {
        //Interaction1
        //When button_ReturnToTheLast clicked change screen to SCREEN_WeWalk
        //Go to SCREEN_WeWalk with no screen transition
        application().gotoSCREEN_WeWalkScreenNoTransition();
    }
}

void Screen_ResearchViewBase::flexButtonCallbackHandler(const touchgfx::AbstractButtonContainer& src)
{
    if (&src == &flexButton_LivePlot)
    {
        //Interaction2
        //When flexButton_LivePlot clicked change screen to SCREEN_WeWalkLivePlot
        //Go to SCREEN_WeWalkLivePlot with no screen transition
        application().gotoSCREEN_WeWalkLivePlotScreenNoTransition();
    }
    if (&src == &flexButton_Settings)
    {
        //Interaction3
        //When flexButton_Settings clicked change screen to Screen_Settings
        //Go to Screen_Settings with no screen transition
        application().gotoScreen_SettingsScreenNoTransition();
    }
    if (&src == &flexButton_START)
    {
        //Interaction5
        //When flexButton_START clicked call virtual function
        //Call EnableAlgorithms
        EnableAlgorithms();
    }
    if (&src == &flexButton_STOP)
    {
        //Interaction6
        //When flexButton_STOP clicked call virtual function
        //Call StopAlgorithms
        StopAlgorithms();
    }
    if (&src == &flexButton_DatalogStart)
    {
        //Interaction7
        //When flexButton_DatalogStart clicked call virtual function
        //Call RequestDatalogStart
        RequestDatalogStart();
    }
    if (&src == &flexButton_DatalogEnd)
    {
        //Interaction8
        //When flexButton_DatalogEnd clicked call virtual function
        //Call RequestDatalogEnd
        RequestDatalogEnd();
    }
    if (&src == &flexButton_GeneralTrigger1)
    {
        //Interaction10
        //When flexButton_GeneralTrigger1 clicked call virtual function
        //Call function_GeneralTrigger1_ButtonClicked
        function_GeneralTrigger1_ButtonClicked();
    }
    if (&src == &flexButton_GeneralTrigger2)
    {
        //Interaction11
        //When flexButton_GeneralTrigger2 clicked call virtual function
        //Call function_GeneralTrigger2_ButtonClicked
        function_GeneralTrigger2_ButtonClicked();
    }
}

void Screen_ResearchViewBase::handleTickEvent()
{
    frameCountInteraction4Interval++;
    if(frameCountInteraction4Interval == TICK_INTERACTION4_INTERVAL)
    {
        //Interaction4
        //When every N tick call virtual function
        //Call UpdateInformation
        UpdateInformation();
        frameCountInteraction4Interval = 0;
    }

    //Interaction9
    //When every N tick call virtual function
    //Call function_UpdateInformation
    function_UpdateInformation();
}
